var path = require('path');
var _ = require('lodash');
var Util = require('machinepack-util');
var Filesystem = require('machinepack-fs');

var pathToTests = path.resolve(inputs.dir, './tests');
var pathToTestSuite = path.resolve(pathToTests, inputs.identity+'.json');

// Build up new test data
var newExpectation = {
  using: inputs.using,
  outcome: inputs.outcome
};
if (!_.isUndefined(inputs.returns)) {
  newExpectation.returns = inputs.returns;
}
// Add before and/or after stringified fns if relevant
if (inputs.before) {
  newExpectation.before = inputs.before;
}
if (inputs.after) {
  newExpectation.after = inputs.after;
}

// Generate unique string from the provided config (`using`).
var newExpectationConfigHash = Util.hash({
  value: inputs.using,
}).execSync();

// console.log('reading test suite from %s',pathToTestSuite);

// Load test suite from disk, or write a JSON file for it
// if one doesn't already exist
Filesystem.ensureJson({
  path: pathToTestSuite,
  schema: {
    machine: inputs.identity,
    expectations: []
  }
}).exec({
  error: function (err){
    return exits.error(err);
  },
  couldNotParse: function (){
    return exits.corrupted(pathToTestSuite);
  },
  success: function (suite){

    // check each test against the provided config
    var matchingExpectation = _.find(suite.expectations, function (expectation){
      var hash = Util.hash({
        value: expectation.using,
      }).execSync();
      return newExpectationConfigHash === hash;
    });

    // if configs DON'T match, add a new test
    if (!matchingExpectation) {
      delete newExpectation.todo;
      suite.expectations.push(newExpectation);
    }
    // if configs match, update the test expectations
    else {
      delete matchingExpectation.todo;
      matchingExpectation.outcome = newExpectation.outcome;
      if (_.isUndefined(newExpectation.returns)) {
        delete matchingExpectation.returns;
      }
      else {
        matchingExpectation.returns = newExpectation.returns;
      }
      // Add before and/or after stringified fns if relevant
      if (inputs.before) {
        matchingExpectation.before = inputs.before;
      }
      if (inputs.after) {
        matchingExpectation.after = inputs.after;
      }

      // Don't write fns if they don't exist
      if (!matchingExpectation.before) {
        delete matchingExpectation.before;
      }
      if (!matchingExpectation.after) {
        delete matchingExpectation.after;
      }
    }

    // Clean out `todo` test stubs
    suite.expectations = _.reject(suite.expectations, function (expectation){
      return !!expectation.todo;
    });

    // Finally, write test suite back to disk
    Filesystem.writeJson({
      json: suite,
      destination: pathToTestSuite,
      force: true
    }).exec({
      error: exits.error,
      success: function (){
        return exits.success();
      }
    });
  }
});

var thisPack = require('../');
var util = require('util');
var browseToUrl = require('open');

thisPack.readPackageJson({
  dir: process.cwd()
}).exec({
  error: exits.error,
  notMachinepack: exits.notMachinepack,
  success: function (machinepack){
    try {

      // TODO: perhaps add other convenient things? See synonyms in the case statement below.
      // Some of that could be broken up or added to.
      var ACCEPTABLE_BROWSE_TO_WHATS = ['docs', 'npm', 'source', 'tests'];
      inputs.toWhat = inputs.toWhat || 'docs';

      // TODO:
      // ideally, we'd ping the various urls w/ a HEAD request and check
      // for a 200 response before opening the browser for a better developer experience

      var url;

      switch(inputs.toWhat.toLowerCase()) {

        case 'docs':
        case 'doc':
        case 'documentation':
        case 'manpages':
        case 'manpage':
        case 'man':
        case 'wiki':
        case 'help':
          if (!machinepack.docsUrl) {
            return exits.noDocsUrl(new Error('This machinepack is not associated with a docs URL (e.g. the URL of a public machinepack on the public machine registry at http://node-machine.org.)'));
          }
          url = machinepack.docsUrl;
          break;

        case 'npm':
        case 'package':
        case 'release':
        case 'version':
        case 'module':
          if (!machinepack.npmUrl) {
            return exits.noNpmUrl(new Error('This machinepack is not associated with a Github repo, or any kind of source code repository at all.'));
          }
          url = machinepack.npmUrl;
          break;

        case 'source':
        case 'lib':
        case 'library':
        case 'history':
        case 'changes':
        case 'issues':
        case 'commits':
        case 'sourcecode':
        case 'repo':
        case 'repository':
        case 'implementation':
        case 'code':
        case 'remote':
          if (!machinepack.sourceUrl) {
            return exits.noSourceUrl(new Error('This machinepack is not associated with a version control (e.g. source code)'));
          }
          url = machinepack.sourceUrl;
          break;

        case 'github':
        case 'hub':
        case 'git':
          if (!machinepack.githubUrl){
            if (!machinepack.githubUrl && machinepack.sourceUrl){
              return exits.noGithubUrl(new Error('This machinepack is not associated with a Github repo- but maybe try '+machinepack.sourceUrl));
            }
            return exits.noSourceUrl(new Error('This machinepack is not associated with a Github repo, or any other kind of version-control/source repository at all.'));
          }
          url = machinepack.githubUrl;
          break;

        case 'travis':
        case 'tests':
        case 'ci':
        case 'test':
        case 'status':
          if (!machinepack.testsUrl) {
            return exits.noTestsUrl(new Error('This machinepack does not have a `testsUrl`- make sure you run `mp scrub`, or manually add the url of your testing/continuous-integration thing to the `machinepack` object in your package.json file.'));
          }
          url = machinepack.testsUrl;
          break;

        default:
          return exits.error(buildError({
            format: ['`mp browse` works w/ no arguments, but if an argument IS provided, it must be one of the following:', ACCEPTABLE_BROWSE_TO_WHATS]
          }));
      }

      browseToUrl(url);
      return exits.success(url);
    }
    catch (e) {
      return exits(e);
    }
  }
});

var path = require('path');
var Arrays = require('machinepack-arrays');
var JSON = require('machinepack-json');
var Util = require('machinepack-util');
var _ = require('lodash');
var thisPack = require('../');

// Resolve absolute path
inputs.dir = path.resolve(process.cwd(), inputs.dir);

// Read local pack
thisPack.readPackageJson({
  dir: inputs.dir
}).exec({
  error: exits.error,
  success: function(packMetadata) {

    // Run some logic (the "iteratee") once for each item of an array.
    Arrays.map({
      array: packMetadata.machines,
      itemExample: {},
      iteratee: function(_inputs, _exits) {

        var machineIdentity = _inputs.item;

        // Read machine file located at the specified path into a JSON string w/ stringified functions.
        thisPack.readMachineFile({
          source: path.resolve(inputs.dir, packMetadata.machineDir, machineIdentity + '.js')
        }).exec({
          error: function (err) {
            // If an expected machine module is corrupted (i.e. it was referenced by
            // `machinepacks.machines` in the package.json file, but the file is all
            // jacked up), then ignore it, but also log a warning.
            console.warn('Ignoring local machine ('+machineIdentity+') b/c it seems to be corrupted. Details:\n',err);
            return _exits.exclude();
          },
          notFound: function (){
            // If an expected machine module is missing (i.e. it was referenced by
            // `machinepacks.machines` in the package.json file), then just ignore it.
            return _exits.exclude();
          },
          success: function (jsonStr){
            // Parse machine data from the JSON-encoded string.
            JSON.parse({
              json: jsonStr,
              schema: {},
            }).exec({
              error: _exits.error,
              success: function  (machineDef) {
                // Make sure machineDef has an identity:
                machineDef.identity = machineDef.identity || machineIdentity;

                return _exits.success(machineDef);
              }
            });
          }
        });
      }
    }).exec({
      error: exits.error,
      success: function(machineDefs) {

        Arrays.map({
          array: machineDefs,
          itemExample: {
            machine: 'some-machine-identity',
            hash: 'a193fha9319vazm31$139a0'
          },
          iteratee: function(_inputs, _exits) {
            var hash = Util.hash({ value: _inputs.item }).execSync();
            return _exits.success({
              hash: hash,
              machine: _inputs.item.identity
            });
          }
        }).exec({
          error: exits.error,
          success: function (machineHashes){
            var hashObj = {
              "identity": packMetadata.npmPackageName,
              "friendlyName": packMetadata.friendlyName,
              "description": packMetadata.description,
              "author": packMetadata.author,
              "license": packMetadata.license,
              "dependencies": packMetadata.machineDependencies,
              "machines": _.pluck(machineHashes, 'hash')
            };
            // Generate unique hash for each machine, and for the top-level pack metadata.
            Util.hash({
              value: hashObj,
            }).exec({
              error: exits.error,
              success: function(packHash){
                // The pack-specific dependencies may be stored in the machinepack.dependencies
                // key of the package.json.  Having them stored in a place separate from the
                // package.json dependencies dictionary allows us to operate on them with changelogs
                // without affecting other required dependencies that are not part of the pack
                // (e.g. sails and its dependencies, for Treeline apps)
                var npmDependencies = packMetadata.machineDependencies || [];
                return exits.success({
                  pack: packMetadata,
                  machines: machineDefs,
                  machineHashes: machineHashes,
                  packHash: packHash,
                  npmDependencies: npmDependencies
                });
              }
            });
          }
        });

      }
    });

  }
});

var path = require('path');
var async = require('async');
var _ = require('lodash');
var rttc = require('rttc');
var Filesystem = require('machinepack-fs');
var NPM = require('machinepack-npm');
var Javascript = require('machinepack-javascript');
var thisPack = require('../');


// `packData` contains basic metadata about the machinepack as well as
// complete metadata about each machine-- including the `fn` (implementation code)
var packData = inputs.packData;

// Ensure packData is valid using an example schema.
packData = rttc.coerce(rttc.infer({
  friendlyName: 'Foo',
  description: 'Node.js utilities for working with foos.',
  author: 'Marty McFly <marty@mcfly.com>',
  license: 'MIT',
  version: '0.5.17',
  id: 'marty/machinepack-do-stuff',
  npmPackageName: '@treelinehq/marty/machinepack-do-stuff',
  dependencies: [ { name: 'lodash', semverRange: '^2.4.1' } ],
  postInstallScript: 'node ./postinstall.js',
  indexJsCode: 'module.exports = require(\'machinepack-util\');',
  machines: [{
    unchanged: false,
    identity: 'do-stuff',
    friendlyName: 'Do stuff',
    description: 'Do stuff given other stuff.',
    extendedDescription: 'Do stuff to the stuff given the other stuff.  If the stuff doesn\'t get done the first time, try it again up to 50 times using an exponential backoff strategy.',
    moreInfoUrl: 'http://place-with-more-info.com',
    cacheable: false,
    sync: false,
    idempotent: false,
    environment: ['sails'],
    inputs: {}, //=> { foo: { friendlyName: 'Foo', example: 'bar' } }
    exits: {}, //=>{ error: { friendlyName: 'error', example: null } }
    fn: '/*the stringified machine fn contents, without the function signature*/',
  }]
}), rttc.dehydrate(packData, true));
// ^Note that we dehydrate before coercing in order to ensure functions are stringified.

// Just in case...
// (i) Lowercase the machine identities
packData.machines = _.map(packData.machines, function (machineDef){
  machineDef.identity = machineDef.identity.toLowerCase();
  return machineDef;
});
// (ii) Also ensure none of the machines now have duplicate identities
//      (if so, then remove them)
packData.machines = _.uniq(packData.machines, 'identity');

// (iii) Use the `id` as the npm package name if no package name is provided:
packData.npmPackageName = packData.npmPackageName || packData.id;

// TODO: enforce all 4 things (^^) in the API when `npmPackageName`s
// are initially set or modified (including if/when they are inferred)

// Names with > 1 level of path separators are not permitted, so transform
// them with dashes
var npmPackageNamePieces = packData.npmPackageName.split('/');
if (npmPackageNamePieces.length > 1) {
  packData.npmPackageName = npmPackageNamePieces.shift() + '/' + npmPackageNamePieces.join('--');
}

// Names with > 0 level of path separators need an @ symbol at the top
if (packData.npmPackageName.split('/').length > 1 && packData.npmPackageName[0] !== '@') {
  packData.npmPackageName = '@' + packData.npmPackageName;
}

// Determine the dictionary that will become the package.json file.
var pkgMetadata = {
  name: packData.npmPackageName,
  private: true,
  version: packData.version || '0.1.0',
  description: packData.description || '',
  keywords: [
    packData.friendlyName,
    'machines',
    'machinepack'
  ],
  author: packData.author,
  license: packData.license,
  dependencies: NPM.unarrayifyDependencies({
    dependencies: packData.dependencies
  }).execSync(),
  devDependencies: {},
  scripts: {},
  machinepack: {
    id: packData.id,
    friendlyName: packData.friendlyName,
