if (inputs.packageName.match(/^@/)){
  return exits.error(new Error('This machine does not currently support scoped packages (e.g. @mikermcneil/foobar)'));
}

Http.sendHttpRequest({
  baseUrl: 'http://registry.npmjs.org',
  url: util.format('/%s', inputs.packageName)
}).exec({
  error: function (err) {
    return exits.error(err);
  },
  notFound: function (response) {
    return exits.packageNotFound(response);
  },
  success: function (response) {
    var parsed;
    try { parsed = JSON.parse(response.body); }
    catch (e) { return exits.couldNotParse(e); }

    return exits.success(parsed);
  }
});

var _ = require('lodash');

// Flatten dependencies.
var flattenedDeps = _.reduce(inputs.dependencies, function (memo, semverRange, name){
  memo.push({
    name: name,
    semverRange: semverRange
  });
  return memo;
}, []);

return exits.success(flattenedDeps);

var _ = require('lodash');

// Un-flatten dependencies.
var dependenciesDict = _.reduce(inputs.dependencies, function (memo, dependency){
  memo[dependency.name] = dependency.semverRange;
  return memo;
}, {});

return exits.success(dependenciesDict);

var path = require('path');
var Proc = require('machinepack-process');
var Filesystem = require('machinepack-fs');

// Ensure specified dir path is absolute by resolving it
// relative to the current working directory.
inputs.dir = path.resolve(inputs.dir);

// Ensure this is a valid package.
Filesystem.readJson({
  source: path.join(inputs.dir, 'package.json'),
  schema: {
    name: 'some-package',
    version: '2.0.0'
  }
}).exec({
  error: exits.error,
  doesNotExist: function (){
    return exits.invalidPackage();
  },
  couldNotParse: function (){
    return exits.invalidPackage();
  },
  success: function (){

    // Run `npm install`
    Proc.spawn({
      command: 'npm install',
      dir: inputs.dir
    }).exec({
      error: function (err){
        try {
          // err.stack
          // err.killed
          // err.signal
          // err.code
          return exits.error(err);
        }
        catch (_e) {
          return exits.error(err||_e);
        }
      },
      notADir: exits.notADir,
      noSuchDir: exits.noSuchDir,
      success: function (bufferedOutput){
        return exits.success();
      }
    });//</after spawning `npm install` as a child process>
  }
});//</after ensuring this is a valid package>

var _ = require('lodash');
var NPM = require('../');

if (inputs.packageName.match(/^@/)){
  return exits.error(new Error('This machine does not currently support scoped packages (e.g. @mikermcneil/foobar)'));
}

NPM.fetchInfo({
  packageName: inputs.packageName
}).exec({
  error: exits.error,
  packageNotFound: exits.packageNotFound,
  couldNotParse: exits.couldNotParse,
  success: function (npmPackageInfo){

    var latestPublishedVersion;
    try {
      var distTags = npmPackageInfo['dist-tags'];
      if (!_.isObject(distTags)) {
        return exits.couldNotParse(new Error('Missing or invalid `dist-tags` received from NPM.'));
      }
      latestPublishedVersion = distTags.latest;
    } catch (e) { return exits.couldNotParse(e); }

    if (!_.isString(latestPublishedVersion)) {
      return exits.couldNotParse(new Error('Missing or invalid `dist-tags.latest` version string received from NPM (it is claiming that `'+latestPublishedVersion+'` is the latest version)'));
    }

    return exits.success(latestPublishedVersion);
  }//</•-upon success::NPM.fetchInfo>
});//</NPM.fetchInfo()>

var _ = require('lodash');
var semver = require('semver');

// Coerce (/validate strict):
//////////////////////////////////////////////////////////////////

// TODO: coerce "almost" version strings, e.g. `1.0`


// Validate:
//////////////////////////////////////////////////////////////////

// Not Blank
//
// i.e. empty string ('')
if (inputs.string.length === 0) {
  return exits.isEmpty();
}

// 20 Characters Max
if (inputs.string.length > 20) {
  return exits.tooLong();
}

// Semantic Version
if (!semver.valid(inputs.string)) {
  return exits.invalidSemanticVersion();
}

// If we made it here, the provided string is valid and good to go!
// (it also might have been coerced a bit)
return exits.success(inputs.string);

var semver = require('semver');

var comparison = semver.compare(inputs.a, inputs.b);

return exits.success(comparison);

