// to a boolean here.  But since we know that machine/rttc take care of that for us,
// we can skip this extra step.
// isCompatible = !!isCompatible;

return exits.success(isCompatible);

var util = require('util');
var _ = require('lodash');
var Http = require('machinepack-http');
var Util = require('machinepack-util');

// TODO: validate start and end date inputs
// TODO: support configurable date range
var now = new Date();
var today = util.format('%s-%s-%s',
  now.getFullYear(),
  (now.getMonth()+1)<10?'0'+(now.getMonth()+1):(now.getMonth()+1),
  now.getDate()<10?'0'+now.getDate():now.getDate()
);
var dateRange = '2012-01-01:'+today;

console.log('date range:\n', dateRange);

// Send an HTTP request and receive the response.
Http.sendHttpRequest({
  method: 'get',
  url: 'http://npm-stat.com/downloads/range/'+dateRange+'/'+inputs.packageName
}).exec({
  // An unexpected error occurred.
  error: function(err) {
    return exits.error(err);
  },
  // OK.
  success: function(result) {

    var parsedData;
    try {
      parsedData = Util.parseJson({
        json: result.body,
        schema: {
          downloads: [{
            day: '2015-01-20',
            downloads: 100
          }]
        }
      }).execSync();
    }
    catch (e) {
      return exits.error(e);
    }

    // Expand the result data
    return exits.success({
      perDay: parsedData.downloads,
      total: _.reduce(parsedData.downloads, function (memo, day){
        return memo + day.downloads;
      }, 0)
    });
  },
});

// Attempt to convert the input value to a number.
var converted = +inputs.string;

// If the result is NaN, Infinity or -Infinity, trigger the `error` exit.
if (isNaN(converted) || converted === Infinity || converted === -Infinity) {
  return exits.error('The given string could not be converted to a number.');
}

// Otherwise return the converted value through the `success` exit.
return exits.success(converted);

